    private void RB5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RB5ActionPerformed
        // TODO add your handling code here:
        if(RB5.isSelected())
        {
            JOptionPane.showMessageDialog(this,"Therefore, a group O individual can receive blood only from a group O individual, but can donate blood to individuals of any ABO blood group (i.e., A, B, O or AB)");
        }
    }//GEN-LAST:event_RB5ActionPerformed

    private void RB6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RB6ActionPerformed
        // TODO add your handling code here:
        if(RB6.isSelected())
        {
            JOptionPane.showMessageDialog(this,"O Negative. As an O Negative blood donor you have a particularly unique opportunity to help people in emergency situations. O Negative blood cells are called “universal”");
        }
    }//GEN-LAST:event_RB6ActionPerformed

